mapper.select:
SELECT u.*
FROM `user` u


        AND u.dept_id = #{deptId}


        AND u.oper_code LIKE CONCAT('%', #{operCode}, '%')


        AND u.oper_name LIKE CONCAT('%', #{operName}, '%')


        AND u.oper_status = #{operStatus}


        AND u.user_type = #{userType}


ORDER BY u.id ASC
